import React from "react";

import { Button, Modal } from "react-bootstrap";

class Wallets extends React.Component {

  constructor(props) {

    super(props);

    this.show = false;

    this.get_Wallets();

  }

  render() {

    return (

      <div id="main">

        <div id="NewWallet" className="Input_DIV">

          <p>Wallet Name:</p>

          <input id="WalletName" ref={value => this.walletName = value} placeholder="Enter Wallet Name" />

          <Button variant="primary" onClick={this.create_New_Wallet.bind(this)}>New Wallet</Button>


        </div>

        {this.wallets_html}


      </div>


    );

  }

  handleClose() {

    this.show = false;

  }

  handleShow() {

    this.show = true;

  }

  async get_Wallets() {

    let response = fetch('getWallets/', {

      method: 'POST',

      body: JSON.stringify({

      }),

    }
    );

    let result = await response.json();

    this.wallets = result;

    for (let wallet in this.wallets) {

      let name = wallet.get("Wallet");

      let balance = wallet.get("Balance");

      let sent = wallet.get("Sent");

      let received = wallet.get("Received");

      let addrs = wallet.get("Addresses");

      this.wallets_html = "<div className='wallet'><div><p>" + name + ":</p><p>" + balance + "</p></div><div><p>Received: " + received + "</p><p>Sent: " + sent + "</p></div><div><h5>Addresses:</h5>";

      for (let addr in addrs) {

        this.wallets_html += "<h6>" + addr + "</h6>";

      }

      this.wallets_html += "</div></div ><div><p>Address: </p><input id='Addr' ref={value => this.addr = value} placeholder='Enter Target Address' /><p>Value: </p><input id='Value' ref={value => this.value = value} placeholder='Enter Value' /><Button variant='primary' onClick={this.create_Transaction({ name })}>Submit Transaction</Button></div>";

    }

  }

  async create_New_Wallet() {

    this.handleClose();

    let response = fetch('createWallet/', {

      method: 'POST',

      body: JSON.stringify({

        wallet_name: this.walletName,

      }),

    }
    );

    let result = await response.json();


  }

  async add_new_address() {

    let response = fetch('createWallet/', {

      method: 'POST',

      body: JSON.stringify({

        wallet: this.walletName,

      }),

    }
    );

    let result = await response.json();


  }

  async create_Transaction(wallet) {

    let response = fetch('startTransaction/', {

      method: 'POST',

      body: JSON.stringify({

        input: wallet,

        output: this.addr,

        value: this.value,

      }),

    }
    );

    let result = await (await response).json();

  }

}

export default Wallets;
