import React from 'react';

import { Button, Tab, Tabs } from 'react-bootstrap';

import "./css/bootstrap.min.css";
import "./css/User.css";

class User extends React.Component {

  render() {

    return (

      <div style={{ position: "relative" }}>

        <div id="User_Title">

          <h3 style={{ paddingTop: "75px" }}>Welcome to Blockchain Wallet</h3>

        </div>

        <div id="Tabs">

          <Tabs className="justify-content-center">

            <Tab eventKey="login" title="Login" className="User_Tab">

              <User_Login />

            </Tab>

            <Tab eventKey="signup" title="New User" className="User_Tab">
              New User
            </Tab>

          </Tabs>

        </div>

      </div>

    );
  }

}

class User_Login extends React.Component {


  constructor(props) {

    super(props);

    this.username = "";

    this.password = "";

  }


  render() {

    return (

      <div className="User_Operation_DIV">

        <div className="Input_DIV">

          <h5>Username:</h5>

          <input id="username" ref={value => this.username = value} placeholder="Enter Username" />

        </div>

        <div className="Input_DIV">

          <h5>Password:</h5>

          <input id="password" type="password" ref={value => this.password = value} placeholder="Enter Password" />

        </div>

        <p className="AlertMsg HiddenAlertMsg" id="InputError">Invalid Username or Password!</p>

        <div className="Input_DIV">

          <Button variant="primary" onClick={this.login.bind(this)}>Login</Button>

        </div>

      </div>

    );


  }

  login() {

    console.log(this.username + ": " + this.password);

  }



}

export default User;
