import React from "react";

import { Button } from "react-bootstrap";

import "./css/bootstrap.min.css";
import "./css/Wallet.css";

class Wallets extends React.Component {

  constructor(props) {

    super(props);

  }

  render() {

    const html_str = this.wallets_html;

    return (

      <div id="main">

        <div id="NewWallet" className="Input_DIV">

          <p>Wallet Name:</p>

          <input id="WalletName" ref={value => this.walletName = value} placeholder="Enter Wallet Name" />

          <Button variant="primary" onClick={this.create_New_Wallet.bind(this)}>New Wallet</Button>

        </div>

        {html_str}

      </div>

    );

  }

  handleClose() {

    this.show = false;

  }

  handleShow() {

    this.show = true;

  }

  async get_Wallets() {

    let response = fetch('getWallets/', {

      method: 'POST',

      body: JSON.stringify({

      }),

    }
    );

    let result = (await response).json();

    result.then((result) => {

      this.wallets = result;

      let m = this.wallets.length;

      for (let i = 0; i < m; i++) {

        let wallet = this.wallets[i];

        let name = wallet["Wallet"];

        let balance = wallet["Balance"];

        let sent = wallet["Sent"];

        let received = wallet["Received"];

        let addrs = wallet["Addresses"];

        this.wallets_html = "<div className='wallet'><div><p>" + name + ":</p><p>" + balance + "</p></div><div><p>Received: " + received + "</p><p>Sent: " + sent + "</p></div><div><h5>Addresses:</h5>";

        let n = addrs.length;

        for (let j = 0; j < n; j++) {

          this.wallets_html += "<h6>" + addrs[j] + "</h6>";

        }

        this.wallets_html += "</div></div ><div><p>Address: </p><input id='Addr' ref={value => this.addr = value} placeholder='Enter Target Address' /><p>Value: </p><input id='Value' ref={value => this.value = value} placeholder='Enter Value' /><Button variant='primary' onClick={this.create_Transaction({ name })}>Submit Transaction</Button></div>";

      }

      let div = document.getElementById("main");

      let element = document.createElement(this.wallets_html);

      div.appendChild(element);

    });

  }

  async create_New_Wallet() {

    this.handleClose();

    let response = fetch('createWallet/', {

      method: 'POST',

      body: JSON.stringify({

        wallet_name: this.walletName,

      }),

    }
    );

    let result = await response.json();


  }

  async add_new_address() {

    let response = fetch('createWallet/', {

      method: 'POST',

      body: JSON.stringify({

        wallet: this.walletName,

      }),

    }
    );

    let result = await response.json();


  }

  async create_Transaction(wallet) {

    let response = fetch('startTransaction/', {

      method: 'POST',

      body: JSON.stringify({

        input: wallet,

        output: this.addr,

        value: this.value,

      }),

    }
    );

    let result = await response.json();

  }

  componentWillMount() {



    this.get_Wallets();

  }

}

export default Wallets;
